# # results
# }
# # pub fn search_case_insensitive<'a>(
# query: &str,
# contents: &'a str,
# ) -> Vec<&'a str> {
# let query = query.to_lowercase();
# let mut results = Vec::new();
# # for line in contents.lines() {
# if line.to_lowercase().contains(&query) {
# results.push(line);
# }
# }
# # results
# }
# # #[cfg(test)]
# mod tests {
# use super::*;
# # #[test]
# fn case_sensitive() {
# let query = "duct";
# let contents = "\
# Rust:
# safe, fast, productive.
# Pick three.
# Duct tape.";
# # assert_eq!(vec!["safe, fast, productive."], search(query, contents));
# }
# # #[test]
# fn case_insensitive() {
# let query = "rUsT";
# let contents = "\
# Rust:
# safe, fast, productive.
# Pick three.
# Trust me.";
# # assert_eq!(
# vec!["Rust:", "Trust me."],
# search_case_insensitive(query, contents)
# );
# }
# }
# # fn main() {} 注意到我們新增了 case_sensitive 欄位並存有布林值。接著，我們需要 run 函式檢查 case_sensitive 欄位的數值並以此決定要呼叫 search 函式或是 search_case_insensitive 函式，如範例 12-22 所示。不過目前還無法編譯。 檔案名稱：src/lib.rs # use std::error::Error;
# use std::fs;
# # pub struct Config {
# pub query: String,
# pub filename: String,
# pub case_sensitive: bool,
# }
# # impl Config {
# pub fn new(args: &[String]) -> Result<Config, &'static str> {
# if args.len() < 3 {
# return Err("not enough arguments");
# }
# # let query = args[1].clone();
# let filename = args[2].clone();
# # Ok(Config { query, filename })
# }
# }
# pub fn run(config: Config) -> Result<(), Box<dyn Error>> { let contents = fs::read_to_string(config.filename)?; let results = if config.case_sensitive { search(&config.query, &contents) } else { search_case_insensitive(&config.query, &contents) }; for line in results { println!("{}", line); } Ok(())
}
# # pub fn search<'a>(query: &str, contents: &'a str) -> Vec<&'a str> {
# let mut results = Vec::new();
# # for line in contents.lines() {
